import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import { Redirect } from "react-router-dom";

export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      islogged: false,
      loginParams: {
        username: "",
        password: "",
      },
    };
  }
  handleInputChange = (e) => {
    let loginParamsNew = { ...this.state.loginParams };
    let val = e.target.value;
    loginParamsNew[e.target.name] = val;
    this.setState({
      loginParams: loginParamsNew,
    });
  };

  handleSubmit = (e) => {
    let username = this.state.loginParams.username;
    let password = this.state.loginParams.password;
    if (username && password) {
      axios
        .post("http://localhost:5000/users/login", this.state)
        .then((res) => {
          console.log(res);
          localStorage.setItem("token", res.data.message);
        })
        .catch((error) => {
          console.log(error);
        });
    }
    e.preventDefault();
  };

  render() {
    if (localStorage.getItem("token")) {
      return <Redirect to="/seeReports" />;
    }

    return (
      <div className="container m-5">
        <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label htmlFor="exampleInputEmail1">User</label>
            <input
              type="text"
              className="form-control"
              name="username"
              value={this.state.username}
              onChange={this.handleInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="exampleInputPassword1">Password</label>
            <input
              type="password"
              className="form-control"
              id="exampleInputPassword1"
              name="password"
              value={this.state.password}
              onChange={this.handleInputChange}
            />
          </div>

          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    );
  }
}
